queries.query:
PREFIX bot: <https://w3id.org/bot#>

SELECT ?z
WHERE {
  GRAPH ?g {
  	?z a bot:Zone .
  }
}

PREFIX bot:   <https://w3id.org/bot#>
PREFIX arch:  <http://architect.com/projects/>
PREFIX props: <https://w3id.org/product/props#>
PREFIX el:    <https://w3id.org/product/BuildingElements#>

SELECT *
WHERE {
	GRAPH arch:17001 {
      ?s a bot:Storey ;
      	bot:containsZone ?z .
	}
}

PREFIX bot: <https://w3id.org/bot#>

SELECT ?site ?zone
WHERE {
  GRAPH ?g {
  	?site a bot:Site ;
      bot:containsZone ?zone .
  }
}

PREFIX bot: <https://w3id.org/bot#>

SELECT ?site ?el
WHERE {
  GRAPH ?g {
  	?site a bot:Site ;
      bot:containsElement ?el .
  }
}

PREFIX bot: <https://w3id.org/bot#>

SELECT ?site ?el
WHERE {
  GRAPH ?g {
  	?site a bot:Site ;
      bot:hasElement ?el .
  }
}

PREFIX bot:  <https://w3id.org/bot#>
PREFIX arch: <http://architect.com/projects/>
PREFIX prod: <https://w3id.org/product#>
PREFIX props: <https://w3id.org/props#>

SELECT ?s ?width
WHERE {
  GRAPH arch:17001 {
    ?s a prod:Wall ;
    	props:dimensionsWidth ?width .
  }
}
